import React, {Component} from 'react'
//import DataService from '../../api/todo/DataService.js'
//import SearchBar  from './SearchBar.jsx'
import axios from 'axios'
//import UpdateBalance from './UpdateBalance.js';

import AuthenticationService from './AuthenticationService.js'
class UserComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            users : [],
            newBalance:"",
            userName: AuthenticationService.getLoggedInUserName()
        };
    }
   
    handleChange = (e) => {
        this.setState(
            {
                [e.target.name]
                  :e.target.value
            }
        )
    }
componentDidMount=()=>{
this.getAllUsers();
this.updateBalance();
}

getAllUsers = () =>{
    axios.get("http://localhost:8080/users")
    //.then(response => console.log(response.data))
    .then(response => response.data)
    .then((response) => {
          this.setState({users : response.data})
     })
}
updateBalance = ()=>{
       
    console.log(this.state.userName);
    console.log(this.state.newBalance);
    axios.put(`${JPA_API_URL}/users/${name}/todos/${id}`, todo);
    axios.put(`http://localhost:8080/users/${this.state.userName}`,45)
    //.then(response => console.log(response.data))
    .then(response => response.data)
    .then((response) => {
        this.setState({users: response.data})
        //console.log(this.state.users)
     })
}

render = ()=> {
    //const user = AuthenticationService.getLoggedInUserName();
    //console.log(this.state.user);
    return (
        <div>
            Welcome {this.state.user}. You can manage your Stocks.
             <h1>User Detail</h1>
             <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>FirstName</th>
                            <th>LastName</th>
                            <th>Balance</th>                   
                        </tr>
                    </thead>
                    <tbody>
                    {
                       
                        this.state.users.map (
                            user =>
                                <tr key={user.userId}>
                                    <td>{user.firstName}</td>
                                    <td>{user.lastName}</td>
                                    <td>{user.balance}</td>
                                </tr>
                                
                        )
                        }
                          Enter balance: <input type="text" name="balance" value={this.state.newBalance} placeholder='Enter balance' onChange={this.handleChange}/>
                                   <button className="btn btn-success" onClick={() => this.updateBalance()}>Update Balance</button>
                        <div>{this.state.userName}</div>

                    </tbody>
                </table>
             </div>
        </div>
    )
  }
}

export default UserComponent
