import React, {Component} from 'react'
//import DataService from '../../api/todo/DataService.js'
import SearchBar  from './SearchBar.jsx'
import axios from 'axios'
import UpdateBalance from './UpdateBalance.js';
import AuthenticationService from './AuthenticationService.js'
class UserComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            users : []
        };
    }
componentDidMount(){
this.getAllUsers();
}
getAllUsers(){
    axios.get("http://localhost:8080/users")
    //.then(response => console.log(response.data))
    .then(response => response.data)
    .then((response) => {
          this.setState({users : response.data})
     })
}
updateBalance(){
    axios.put(`${"http://localhost:8080"}/users/${this.username}/todos/${balance}`)
    //.then(response => console.log(response.data))
    .then(response => response.data)
    .then((response) => {
          this.setState({users : response.data})
     })
}

render() {
    const user = AuthenticationService.getLoggedInUserName();
    console.log(user);
    return (
        <div>
             <h1>User Detail</h1>
             <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>FirstName</th>
                            <th>LastName</th>
                            <th>Balance</th>                   
                        </tr>
                    </thead>
                    <tbody>
                    {
                       
                        this.state.users.map (
                            user =>
                                <tr key={user.userId}>
                                    <td>{user.firstName}</td>
                                    <td>{user.lastName}</td>
                                    <td>{user.balance}</td>
                                   {/* <td>Enter balance: <input type="text" name="balance" value={user.balance} placeholder='Enter balance' onChange={this.handleChange}/></td> 
                                   <button className="btn btn-success" onClick={() => this.updateBalance(user.balance)}>Update Balance</button> */}
                                </tr>
                                
                        )
                        }
                          Enter balance: <input type="text" name="balance" value={user.balance} placeholder='Enter balance' onChange={this.handleChange}/>
                                   <button className="btn btn-success" onClick={() => this.updateBalance(user.balance)}>Update Balance</button>
                        <div>{user}</div>
                    </tbody>
                </table>
             </div>
        </div>
    )
  }
}

export default UserComponent
