import React, {Component} from 'react'
//import DataService from '../../api/todo/DataService.js'
//import SearchBar  from './SearchBar.jsx'
import axios from 'axios'
//import UpdateBalance from './UpdateBalance.js';

import AuthenticationService from './AuthenticationService.js'
class UserComponent extends Component {
    constructor(props){
        super(props)
        this.state = {
            users : [],
            newBalance: 0.0,
            user:""
        };
        this.updateBalance = this.updateBalance.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }
    handleNameChange = user => {
        this.setState({ name })
      }
    handleChange = (e) => {
        this.setState(
            {
                [e.target.name]
                  :e.target.value
            }
        )
    }
componentDidMount(){
this.getAllUsers();
}
getAllUsers = () =>{
    axios.get("http://localhost:8080/users")
    //.then(response => console.log(response.data))
    .then(response => response.data)
    .then((response) => {
          this.setState({users : response.data})
     })
}
updateBalance(){
    let u = AuthenticationService.getLoggedInUserName();
   this.setState({user:this.u})
     console.log(this.state.user);
    axios.put(`${"http://localhost:8080"}/users/${this.state.user}/${this.state.newbalance}`)
    //.then(response => console.log(response.data))
    .then(response => response.data)
    .then((response) => {
          this.setState({users : response.data})
     })
}

render() {
    //const user = AuthenticationService.getLoggedInUserName();
    //console.log(this.state.user);
    return (
        <div>
             <h1>User Detail</h1>
             <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>FirstName</th>
                            <th>LastName</th>
                            <th>Balance</th>                   
                        </tr>
                    </thead>
                    <tbody>
                    {
                       
                        this.state.users.map (
                            user =>
                                <tr key={user.userId}>
                                    <td>{user.firstName}</td>
                                    <td>{user.lastName}</td>
                                    <td>{user.balance}</td>
                                   {/* <td>Enter balance: <input type="text" name="balance" value={user.balance} placeholder='Enter balance' onChange={this.handleChange}/></td> 
                                   <button className="btn btn-success" onClick={() => this.updateBalance(user.balance)}>Update Balance</button> */}
                                </tr>
                                
                        )
                        }
                          Enter balance: <input type="text" name="balance" value={this.state.newBalance} placeholder='Enter balance' onChange={this.handleChange}/>
                                   <button className="btn btn-success" onClick={() => this.updateBalance()}>Update Balance</button>
                        <div>{this.state.user}</div>
                    </tbody>
                </table>
             </div>
        </div>
    )
  }
}

export default UserComponent
